class Ledconcretefsm (name: String, scope: CoroutineScope  ) : ActorBasicFsm( name, scope ){

	private var ledstate  = 0  // 0 off 1 blink 2 on

	override fun getInitialState() : String{
		return "s0"
	}
	override fun getBody() : (ActorBasicFsm.() -> Unit){
		return { //this:ActionBasciFsm
				state("s0") { //this:State
					action { //it:State
						println("$name | STARTS")
					}
					 transition( edgeName="goto",targetState="waiting", cond=doswitch() )
				}	 
				state("waiting") { //this:State
					action { //it:State
					}
					 transition(edgeName="t02",targetState="on",cond=whenDispatch("ledon"))
					transition(edgeName="t03",targetState="off",cond=whenDispatch("ledoff"))
					transition(edgeName="t04",targetState="blink",cond=whenDispatch("ledblink"))
				}	 
				state("on") { //this:State


					action { //it:State
						try {
							Runtime.getRuntime().exec("sudo bash led25GpioTurnOn.sh")

						} catch (e: Exception) {
							println("WARNING: ledConcrete does not find led25GpioTurnOn.sh")
						}
						ledstate = 2
					}
					 transition( edgeName="goto",targetState="waiting", cond=doswitch() )


				}
				state("off") { //this:State
					action { //it:State

						try {
							Runtime.getRuntime().exec("sudo bash led25GpioTurnOff.sh")

