							} catch (e: Exception) {
								println("WARNING: ledConcrete does not find led25GpioBlink.sh")
							}
							ledstate = 1
						}
					}

					 transition( edgeName="goto",targetState="waiting", cond=doswitch() )
				}	 
			}
		}
